module.exports = function getPageConfig (siteId, tags) {
  return {
    protocol: 'http',
    server: 'adserver.adtech.de',
    network: '1135.1', /* Swiss Adserver - NZZ-Network */
    siteid: siteId,
    fif: { usefif: true },
    params: {
      loc: '100',
    },
    kv: {
      tags: tags
    }
  };

};
