var _ = require('lodash');

var pageTypeMap = {
  "newsarticle": "Artikel",
  "ressort": "Ressort",
  "home": "Home",
  // static key name still needs to be defined by Backend
  "static": "Static"
};


var getPageConfigByType = function (pageType, config) {
  var pageConfig = _.where(config.page_types, {'siteName': pageType});
  if (pageConfig.length) {
    return pageConfig[0];
  }
};



var getPageSlots = function (pageKind, config) {
  var type = "Home";

  if (pageKind && pageKind.length) {
    type = pageTypeMap[pageKind];
  }
  return getPageConfigByType(type, config);
};

module.exports = {
  getPageSlots: getPageSlots
};
