'use strict';

const React = require('react');

const ColorPalette = require('styleguide/color-palette');

var data = [];

try {
  data = require('./data.json');
} catch (e) {
  throw e;
}

const Colors = React.createClass({

  render: function() {
    return (
      <div>
        {data.map((palette, index) =>
          <ColorPalette key={index} {...palette} />
        )}
      </div>
    );
  }

});

module.exports = Colors;
