'use strict';

const React = require('react');

const TypographyItem = require('styleguide/typography-item');

const Typography = React.createClass({

  render() {
    return (
      <div>
        {this.props.sections.map((section, index) =>
          <TypographyItem key={index} {...section.props}>
            <div dangerouslySetInnerHTML={{ __html: section.content }} />
          </TypographyItem>
        )}
      </div>
    );
  }

});

module.exports = Typography;
