import React from 'react';
import bem from 'react-bem-helper';

const classes = new bem({
  name: 'ad'
});

export default React.createClass({

  displayName: 'Ad',

  propTypes: {
    placementId: React.PropTypes.string.isRequired
  },

  render() {
    return (
      <div id={this.props.placementId} {...classes()}></div>
    );
  }

});
