import React from 'react';
import bem from 'react-bem-helper';


const classes = new bem({
  name: 'article-header',
  prefix: ''
});

export default React.createClass({

  displayName: 'ArticleByline',

  propTypes: {
    author: React.PropTypes.string.isRequired,
    source: React.PropTypes.string
  },

  render() {
    var props = this.props;
    var source = props.source ? props.source : null;
    var author = props.author ? 'von ' + props.author : null;

    if (props.source && props.author) {
      source = ' â€” ' + props.source;
    }

    return (
      <p itemProp='author' {...classes('author', null)}>
        {author}{source}
      </p>
    );
  }

});
