import createComponent from 'utils/test/create-component';

import ArticleByline from 'components/article-byline';
import React from 'react/addons';

import data from 'components/article-byline/.variations';
import { expect } from 'chai';


describe('Article Byline', function() {

  var byline, renderedByline;

  beforeEach(function() {
    byline = createComponent(ArticleByline, data);
    renderedByline = React.renderToString(React.createElement(ArticleByline, data));
  });

  describe('renders', function() {

    it('p wrapper', () => {
      expect(byline.type).to.equal('p');
    });

    it('itemprop attribute contains author', () => {
      expect(renderedByline).to.contain('itemprop="author"');
    });

  });
});
