import React from 'react';
import bem from 'react-bem-helper';

const classes = new bem({
  name: 'article-comments',
  prefix: ''
});

export default React.createClass({

  getDefaultState() {


  },

  loadDisqus(){
    // CONFIGURATION VARIABLES
    var disqus_shortname = 'aargauerzeitung';
    var doc = window.document;
    // DON’T EDIT BELOW THIS LINE
      var dsq = doc.createElement('script'); dsq.type = 'text/javascript'; dsq.async = true;
      dsq.src = '//‚ + disqus_shortname + ‚.disqus.com/embed.js';
      (doc.getElementsByTagName('head')[0] || doc.getElementsByTagName('body')[0]).appendChild(dsq);
  },

  render() {

    return (
      <div {...classes()}>
        <button type='button' onClick={this.loadDisqus.bind()}>Show comments</button>
        <p>Comments go here.</p>
      </div>
    );
  }

});
