import React from 'react';
import bem from 'react-bem-helper';

import DataMap from 'higher-order/data/article-content';
import MainAsset from 'components/main-asset';
import ArticleBody from 'components/article-body';
import Tags from 'components/tags';

const classes = new bem({
  name: 'article-content',
  prefix: ''
});

const ArticleContent = React.createClass({

  propTypes: {
    asset: React.PropTypes.element.isRequired,
    teaser: React.PropTypes.string.isRequired,
    text: React.PropTypes.string.isRequired,
    enrichments: React.PropTypes.array,
    tags: React.PropTypes.array
  },

  render() {
    return (
      <div {...classes()}>
        <MainAsset asset={this.props.asset} />
        <p itemProp='description' {...classes('intro', null, 'type-intro')}>{this.props.teaser}</p>
        <ArticleBody text={this.props.text} enrichments={this.props.enrichments} />
        <Tags items={this.props.tags} />
      </div>
    );
  }

});

export default DataMap(ArticleContent);
