import React from 'react';

export default Component => React.createClass({

  displayName: 'ArticleContentWrapper',

  propTypes: {
    assets: React.PropTypes.array.isRequired,
    lead: React.PropTypes.string.isRequired,
    text: React.PropTypes.string.isRequired,
    stoerer: React.PropTypes.arrayOf(React.PropTypes.object).isRequired,
    keywords: React.PropTypes.arrayOf(React.PropTypes.string).isRequired
  },

  mappedData() {
    var props = this.props;

    return {
      mainAsset: props.assets[0],
      enrichments: props.stoerer,
      intro: props.lead,
      text: props.text,
      tags: props.keywords
    };
  },

  render() {
    return (
      <Component {...this.mappedData()} />
    );
  }

});
