import React from 'react';
import bem from 'react-bem-helper';

import ArticleHeader from 'components/article-header';
import MainAsset from 'components/main-asset';
import ArticleBody from 'components/article-body';

const classes = new bem({
  name: 'article-detail',
  prefix: ''
});

export default React.createClass({

  displayName: 'ArticleDetail',

  propTypes: {
    header: React.PropTypes.object.isRequired,
    mainAsset: React.PropTypes.object,
    body: React.PropTypes.object.isRequired
  },

  render() {
    return (
      <article {...classes()} itemScope itemType='http://schema.org/Article'>
        <ArticleHeader {...this.props.header} />
        {this.props.mainAsset ? <MainAsset asset={this.props.mainAsset} /> : ''}
        <ArticleBody {...this.props.body} />
      </article>
    );
  }

});
