import React from 'react';
import bem from 'react-bem-helper';

import DateFormatter from 'components/date-formatter';

const classes = new bem({
  name: 'article-header',
  prefix: ''
});

export default React.createClass({

  displayName: 'ArticleHeader',

  propTypes: {
    catchword: React.PropTypes.string.isRequired,
    title: React.PropTypes.string.isRequired,
    author: React.PropTypes.string.isRequired,
    source: React.PropTypes.string.isRequired,
    published: React.PropTypes.string.isRequired,
    modified: React.PropTypes.string.isRequired
  },

  render() {
    return (
      <header {...classes()}>
        <p itemProp='genre' {...classes('catchword', null, 'type-catchword')}>{this.props.catchword}</p>
        <h1 itemProp="headline" {...classes('title', null, 'type-h1')}>{this.props.title}</h1>
        <div {...classes('meta', null, 'type-detail')}>
          <p itemProp='author' {...classes('author', null)}>von {this.props.author} â€“ {this.props.source}</p>
          <p {...classes('date')}>
            <DateFormatter
              publicationDate={this.props.published}
              modificationDate={this.props.modified}
            />
          </p>
        </div>
      </header>
    );
  }

});
