import React from 'react';
import bem from 'react-bem-helper';

const classes = new bem({
  name: 'article-header',
  prefix: ''
});

export default React.createClass({

  propTypes: {
    data: React.PropTypes.object.isRequired
  },

  formatDate(date) {
    var d = date ? new Date(date) : new Date();

    return d.getDate()
      + '.' + d.getMonth()
      + '.' + d.getFullYear()
      + ' ' + d.getHours()
      + ':' + d.getMinutes() + ' Uhr';
  },

  render() {

    return (
      <header {...classes()}>
        <p itemProp='genre' {...classes('catchword', null, 'type-tiny-header')}>{this.props.data.context_label}</p>
        <h1 itemProp="headline" {...classes('title', null, 'type-h1')}>{this.props.data.title}</h1>
        <div {...classes('meta', null, 'type-detail')}>
          <p itemProp='author' {...classes('author', null)}>{this.props.data.author} â€“ {this.props.data.source.title}</p>
          {this.props.data.dc.modified && this.props.data.dc.modified !== this.props.data.dc.effective
            ? <p itemProp='dateModified' {...classes('date')}>Zuletzt aktualisiert am {this.formatDate(this.props.data.dc.modified)}</p>
            : <p itemProp='dateCreated' {...classes('date')}>{this.formatDate(this.props.data.dc.effective)}</p>
          }
        </div>
      </header>
    );
  }

});
