'use strict';

const React = require('react');
const bem = require('react-bem-helper');

const classes = new bem({
  name: 'article-header',
  prefix: ''
});

const ArticleHeader = React.createClass({

  render() {
    return (
      <header {...classes()}>
        <p {...classes('catchword', null, 'type-tiny-header')}>{this.props.spitzmarke}</p>
        <h1 {...classes('title', null, 'type-h1')}>{this.props.title}</h1>
        <div {...classes('meta', null, 'type-detail')}>
          <p {...classes('author', null)}>{this.props.author}</p>
          <p {...classes('date', 'creation')}>{this.props.createdate}</p>
          {this.props.modifieddate !== this.props.createdate ? <p {...classes('date', 'update')}>{this.props.modifieddate}</p> : ''}
        </div>
        {this.props.children}
      </header>
    );
  }

});

module.exports = ArticleHeader;
