import React from 'react';

export default Component => React.createClass({

  displayName: 'ArticleHeaderWrapper',

  propTypes: {
    article: React.PropTypes.object.isRequired
  },

  mappedData() {
    var article = this.props.article;

    return {
      catchword: article.context_label,
      title: article.title,
      author: article.author,
      source: article.source.title || (article.source.source ? article.source.source.title : null),
      published: article.dc.effective,
      modified: article.dc.modified
    };
  },

  render() {
    return (
      <Component {...this.mappedData()} />
    );
  }

});
