import React from 'react';

import ArticleHeader from 'components/article-header';

export default Component => React.createClass({

  propTypes: {
    context_label: React.PropTypes.string.isRequired,
    title: React.PropTypes.string.isRequired,
    author: React.PropTypes.string.isRequired,
    source: React.PropTypes.object.isRequired,
    dc: React.PropTypes.object.isRequired,
  },

  mappedData() {
    var props = this.props;

    return {
      catchword: props.context_label,
      title: props.title,
      author: props.author,
      source: props.source.title || (props.source.source ? props.source.source.title : null),
      published: props.dc.effective,
      modified: props.dc.modified
    };
  },

  render() {
    return (
      <ArticleHeader {...this.mappedData()} />
    );
  }

});
