import React from 'react';
import bem from 'react-bem-helper';

import Intro from 'components/intro';
import MainAsset from 'components/main-asset';
import SocialButtons from 'components/social-buttons';

const classes = new bem({
  name: 'article-intro',
  prefix: ''
});

export default React.createClass({

  displayName: 'ArticleIntro',

  propTypes: {
    intro: React.PropTypes.string.isRequired,
    mainAsset: React.PropTypes.object,
    socialButtons: React.PropTypes.object.isRequired,
  },

  render() {
    var { intro, mainAsset, socialButtons } = this.props;

    return (
      <div {...classes()}>
        {mainAsset ? <MainAsset asset={mainAsset} /> : ''}
        <div {...classes('lower')}>
          <SocialButtons {...socialButtons} orientation='vertical' modifier='article' />
          <Intro text={intro} />
        </div>
      </div>
    );
  }

});
