var helpers = require('helpers');

var parsedArticle = function(article) {
  return {
    intro: article.lead,
    mainAsset: helpers.getMainAsset(article.assets)
  };
};

var parsedData = function(state) {
  return parsedArticle(state.article);
};

module.exports = {
  iso: true,
  data: parsedData
};

