var getMainAsset = require('helpers/get-main-asset');

module.exports = function(state) {

  var parsedData = function(article) {
    return {
      intro: article.lead,
      mainAsset: getMainAsset(article.assets),
      socialButtons: {
        title: article.title,
        description: article.lead
      }
    };
  };

  return parsedData(state.article);

};
