var getMainAsset = require('helpers/asset/get-main-asset');

var parsedArticle = function(article, path) {
  return {
    intro: article.lead,
    mainAsset: getMainAsset(article.assets),
    path: path
  };
};

var parsedData = function(state) {
  return parsedArticle(state.article, state.path);
};

module.exports = {
  iso: true,
  data: parsedData,
};

