var getMainAsset = require('helpers/asset/get-main-asset');

var parsedArticle = function(article) {
  return {
    intro: article.lead,
    mainAsset: getMainAsset(article.assets)
  };
};

var parsedData = function(state) {
  return parsedArticle(state.article);
};

module.exports = {
  iso: true,
  data: parsedData
};

