/* eslint-disable no-unused-expressions */

import slot from 'components/article-intro/slot';
import state from 'utils/data/master-article';

import { expect } from 'chai';

describe('ArticleIntro', function() {

  describe('slot', function() {

    let fixture;

    beforeEach(function() {
      fixture = slot.data(state);
    });

    it('sets iso rendering', function() {
      expect(slot.iso).to.be.true;
    });

    it('returns data', function() {
      expect(fixture).to.be.an('object');
    });

    it('data has an intro', function() {
      expect(fixture.intro).to.be.a('string');
    });

    it('data has a main asset', function() {
      expect(fixture.mainAsset).to.be.an('object');
    });

    it('selects main asset correctly', function() {
      expect(fixture.mainAsset).to.be.an('object');
      expect(fixture.mainAsset).to.equal(state.article.assets[0]);
    });

  });

});
