import React from 'react';
import bem from 'react-bem-helper';

const classes = new bem({
  name: 'avatar',
  prefix: ''
});

export default React.createClass({

  displayName: 'Avatar',

  propTypes: {
    src: React.PropTypes.string,
    alt: React.PropTypes.string,
    title: React.PropTypes.string,
    link: React.PropTypes.string,
    itemProp: React.PropTypes.string
  },

  render() {

    const { src, alt, title, link, itemProp } = this.props;

    if (!src) {
      return null;
    }

    let image = (
      <img {...classes('image')}
        src={src}
        alt={alt}
        itemProp={itemProp}
      />
    );

    if (link) {
      return (
        <a {...classes('', 'link')} href={link} title={title}>
          { image }
        </a>
      );
    }

    return (
      <span {...classes('')}>
        { image }
      </span>
    );
  }

});
