import React from 'react';
import bem from 'react-bem-helper';

import Icon from 'components/icon';

const classes = new bem({
  name: 'button',
  prefix: ''
});

export default React.createClass({

  propTypes: {
    label: React.PropTypes.string.isRequired,
    path: React.PropTypes.string,
    modifier: React.PropTypes.string,
    attributes: React.PropTypes.object,
    icon: React.PropTypes.string
  },

  render() {

    // if (this.props.path) {

    //   return (
    //     <a {...this.props.attributes} href={this.props.path} {...classes(null, this.props.modifier)}>
    //       {this.props.icon
    //        ? <Icon id={this.props.icon} />
    //        : ''}

    //       {this.props.label}
    //     </a>
    //   );

    // } else {

    //   return (
    //     <button {...this.props.attributes} {...classes(null, this.props.modifier)}>
    //       {this.props.icon
    //        ? <Icon id={this.props.icon} />
    //        : ''}

    //       {this.props.label}
    //     </button>
    //   );

    // }


    var content;

    if (this.props.path) {
      content =
        <a {...this.props.attributes} href={this.props.path} {...classes(null, this.props.modifier)}>
          {this.props.icon
           ? <Icon id={this.props.icon} />
           : ''}

          {this.props.label}
        </a>;
    } else {
      content =
        <button {...this.props.attributes} {...classes(null, this.props.modifier)}>
          {this.props.icon
           ? <Icon id={this.props.icon} />
           : ''}

          {this.props.label}
        </button>;
    }

    return content;

  }

});
