import React from 'react';
import bem from 'react-bem-helper';

import Icon from 'components/icon';

const classes = new bem({
  name: 'button',
  prefix: ''
});

export default React.createClass({

  propTypes: {
    label: React.PropTypes.string,
    icon: React.PropTypes.string,
    modifier: React.PropTypes.string,
    attributes: React.PropTypes.object,
    // `_target` is either a string (for a link)
    // or a function (for a button)
    // leading underscore meant not to be confused with `target` HTML attribute
    _target: React.PropTypes.oneOfType([
      React.PropTypes.string,
      React.PropTypes.func
    ]).isRequired
  },

  render() {

    if (!this.props.label && !this.props.icon) {
      return false;
    }

    var content;
    var modifiers = {
      icon: this.props.icon && !this.props.label,
      [this.props.modifier]: !!this.props.modifier
    };

    if (typeof this.props._target === 'string') {
      content =
        <a href={this.props._target} {...this.props.attributes} {...classes(null, modifiers)}>
          {this.props.icon ? <Icon id={this.props.icon} /> : ''}
          {this.props.label}
        </a>;
    } else {
      content =
        <button onClick={this.props._target} {...this.props.attributes} {...classes(null, modifiers)}>
          {this.props.icon ? <Icon id={this.props.icon} /> : ''}
          {this.props.label}
        </button>;
    }

    return content;

  }

});
