import React from 'react';
import bem from 'react-bem-helper';

import Container from 'components/container';
import Teaser from 'components/teaser';
import Grid from 'components/grid';

const classes = new bem({
  name: 'category-overview',
  prefix: ''
});

export default React.createClass({

  displayName: 'CategoryOverview',

  propTypes: {
    teasers: React.PropTypes.array.isRequired
  },

  getDefaultProps() {
    return {
      category: 'meinung'
    };
  },

  mapGridTeasers(teasers) {
    return teasers.map((teaser) => {
      return (
        <Teaser {...teaser} />
      );
    });
  },

  render() {

    const { teasers, category } = this.props;

    return (
      <section {...classes('', category)}>
        <Container>
          <Grid items={this.mapGridTeasers(teasers)} modifier={'three-up-simple'} />
        </Container>
      </section>
    );
  }

});
