var coverHelpers = require('helpers/cover');

var parsedCoverForVariation = function(state) {
  var variation = state.variation;

  if (variation === 'newsletter') {
    return coverHelpers.getNewsletterProps;
  }

  return coverHelpers.getFromDossier(state.dossier, variation);
};

var parsedData = function(state) {
  return parsedCoverForVariation(state);
};

module.exports = {
  iso: true,
  data: parsedData
};
