import React from 'react';
import _ from 'lodash';

import getTeaserFromArticle from 'helpers/get-teaser-from-article';

export default Component => React.createClass({

  displayName: 'CuratedRecommendationsWrapper',

  propTypes: {
    teasers: React.PropTypes.arrayOf(React.PropTypes.object).isRequired
  },

  mappedTeasers(teasers) {
    return teasers.map((teaser) => {
      return getTeaserFromArticle({
        article: teaser,
        variation: 'light-boxed'
      });
    });
  },

  mappedData(props) {
    return _.assign({}, props, { articles: this.mappedTeasers(props.teasers) });
  },

  render() {
    return (
      <Component {...this.mappedData(this.props)} />
    );
  }

});
