import React from 'react';

export default React.createClass({
  displayName: 'DossierHeader',

  render() {

    if (!this.props.dossier) {
      return (<div>Dossier not found!</div>);
    }

    return (
      <div>
        <h1>{this.props.dossier.title}</h1>
        <p>{this.props.dossier.header.lead}</p>
      </div>
    );

  }
});
