import React from 'react' ;
import bem from 'react-bem-helper';

import Tags from 'components/tags';
import ResponsiveImage from 'components/responsive-image';

const classes = new bem({
  name: 'dossier-header',
  prefix: ''
});

export default React.createClass({

  displayName: 'DossierHeader',

  propTypes: {
    title: React.PropTypes.string.isRequired,
    text: React.PropTypes.string.isRequired,
    background: React.PropTypes.string,
    image: React.PropTypes.object,
    tags: React.PropTypes.arrayOf(React.PropTypes.string)
  },

  render() {

    const { title, text, background, image, tags } = this.props;

    const heroBg = {
      backgroundImage: `url(${background})`
    };

    return (
      <header {...classes()}>
        <div {...classes('hero')} style={background ? heroBg : null}>
          <div {...classes('hero-text')}>
            <p {...classes('catchword')}>Dossier</p>
            <h1 {...classes('title')}>{ title }</h1>
          </div>
        </div>
        <div {...classes('summary')}>
          <div {...classes('summary-wrapper')}>
            {image
              ? <aside {...classes('summary-aside')}>
                  <div {...classes('thumbnail')}>
                    <ResponsiveImage {...image} ratio='1x1' size='narrow' fill='true' />
                  </div>
                </aside>
            :null}
            <div {...classes('summary-content')}>
              <h2 {...classes('summary-title')}>Zusammenfassung</h2>
              <div {...classes('summary-copy')} dangerouslySetInnerHTML={{ __html: text }} />
              <div {...classes('related')}>
                <Tags items={tags} />
              </div>
            </div>
          </div>
        </div>
      </header>
    );
  }

});
