import React from 'react' ;

import Container from 'components/container';
import getMainAsset from 'helpers/get-main-asset';

export default React.createClass({

  displayName: 'DossierHeader',

  propTypes: {
    header: React.PropTypes.object
  },

  render() {

    const { header } = this.props;

    var mainAsset = getMainAsset(header.assets);

    const backgroundImage = {
      backgroundImage: `url(${mainAsset.image_url})`
    };

    return (
      <header className='dossier-header'>
        <div className='dossier-header__hero' style={ backgroundImage }>
          <div className='dossier-header__hero-text'>
            <p className='dossier-header__hero-subtitle'>Dossier</p>
            <h1 className='dossier-header__hero-title'>{ header.title }</h1>
          </div>
        </div>
        <div className='dossier-header__summary'>
          <Container>
            <div className='dossier-header__left-wrapper'>
              <div className='dossier-header__summary-image' style={ backgroundImage } />
            </div>
            <div className='dossier-header__right-wrapper'>
              <h2 className='dossier-header__summary-title'>Zusammenfassung</h2>
              <div className='dossier-header__summary-copy'>{ header.text }</div>
              <div className='dossier-header__related'>
                <h3>Verwandte Themen</h3>
                <ul>
                  <li><a href="/">Greichenland,</a></li>
                  <li><a href="/">Eurozone,</a></li>
                  <li><a href="/">Irland,</a></li>
                  <li><a href="/">Merkel</a></li>
                </ul>
              </div>
            </div>
          </Container>
        </div>
      </header>
    );
  }

});
