import React from 'react';
import bem from 'react-bem-helper';
import Audio from 'components/audio';

import { getEnrichmentPlacement } from 'helpers/enrichment';
import ResponsiveVideoMixin from 'mixins/responsive-video';

const classes = new bem({
  name: 'enrichment-audio',
  prefix: ''
});

export default React.createClass({

  displayName: 'EnrichmentAudio',

  mixins: [ResponsiveVideoMixin],

  propTypes: {
    placement: React.PropTypes.string.isRequired
  },

  render() {
    return (
      <div {...classes(null, getEnrichmentPlacement(this.props.placement))}>
        <Audio {...this.props.asset} autoPlay={false} />
      </div>
    );
  }

});
