import React from 'react';
import bem from 'react-bem-helper';
import Audio from 'components/audio';

import Wrapper from './wrapper';

import getEnrichmentPlacement from 'helpers/get-enrichment-placement';
import ResponsiveVideoMixin from 'mixins/responsive-video';

const classes = new bem({
  name: 'enrichment-audio',
  prefix: ''
});

const EnrichmentAudio = React.createClass({

  displayName: 'EnrichmentAudio',

  mixins: [ResponsiveVideoMixin],

  propTypes: {
    kalturaID: React.PropTypes.string.isRequired,
    accountID: React.PropTypes.string.isRequired,
    playerID: React.PropTypes.string.isRequired,
    meta: React.PropTypes.object.isRequired,
    caption: React.PropTypes.object,
    placement: React.PropTypes.string.isRequired
  },

  render() {
    return (
      <div {...classes(null, getEnrichmentPlacement(this.props.placement))}>
        <Audio {...this.props.asset} autoPlay={false} />
      </div>
    );
  }

});

export default Wrapper(EnrichmentAudio);
