import React from 'react';
import { getFileExtension } from 'helpers/mime-types';

export default Component =>  React.createClass({

  displayName: 'EnrichmentDocumentWrapper',

  propTypes: {
    asset: React.PropTypes.object,
    title: React.PropTypes.string.isRequired,
    description: React.PropTypes.string,
    placement: React.PropTypes.string,
    size: React.PropTypes.number,
    fileType: React.PropTypes.string
  },

  mappedData(props) {

    var asset = props.asset;
    var assetInfo = asset ? asset.info : null;
    var fileType = props.fileType ? props.fileType : null;

    if (assetInfo && assetInfo.content_type) {
      fileType = getFileExtension(assetInfo.content_type);
    }

    return {
      path: props.path || asset.download_url,
      title: props.title || asset.title,
      size: props.size || assetInfo.content_length,
      fileType: fileType
    };

  },

  render() {
    return (
      <Component {...this.mappedData(this.props)} />
    );
  }

});
