import React from 'react';

export default Component =>  React.createClass({

  displayName: 'EnrichmentDocumentWrapper',

  propTypes: {
    asset: React.PropTypes.object,
    title: React.PropTypes.string.isRequired,
    description: React.PropTypes.string,
    placement: React.PropTypes.string,
    size: React.PropTypes.number
  },

  mappedData(props) {

    var asset = props.asset;

    return {
      path: props.path || asset.download_url,
      title: props.title || asset.title,
      size: props.size || asset.content_length
    };

  },

  render() {
    return (
      <Component {...this.mappedData(this.props)} />
    );
  }

});
