import React from 'react';
import bem from 'react-bem-helper';

import getEnrichmentPlacment from 'helpers/get-enrichment-placement';
import ImageAsset from 'components/image-asset';

const classes = new bem({
  name: 'enrichment-image',
  prefix: ''
});

export default React.createClass({

  displayName: 'EnrichmentImage',

  propTypes: {
    asset: React.PropTypes.object,
    title: React.PropTypes.string,
    description: React.PropTypes.string,
    image_url: React.PropTypes.string,
    placement: React.PropTypes.string
  },

  render() {
    return (
      <div {...classes(null, getEnrichmentPlacment(this.props.placement))}>
        <ImageAsset asset={this.props} size={getEnrichmentPlacment() === 'full' ? 'max' : 'small'} ratio="16x9"/>
      </div>
    );
  }

});
