import React from 'react';

import EnrichmentImage from 'components/enrichment-image';

export default Component => React.createClass({

  propTypes: {
    asset: React.PropTypes.object.isRequired,
    title: React.PropTypes.string,
    description: React.PropTypes.string,
    placement: React.PropTypes.string,
  },

  mappedData() {

    var props = this.props;
    var asset = props.asset;
    var size = this.props.placement && ['left', 'right'].indexOf(this.props.placement) > -1
      ? 'small'
      : 'max';

    return {
      title: props.title || asset.title,
      description: props.description || asset.description,
      url: asset.image_url,
      size: size,
      source: asset.source.title || (asset.source.source ? asset.source.source.title : false)
    };

  },

  render() {
    return (
      <EnrichmentImage {...this.mappedData()} />
    );
  }

});
