import React from 'react';
import bem from 'react-bem-helper';

import Caption from 'components/caption';

const classes = new bem({
  name: 'enrichment-snippet',
  prefix: ''
});

export default React.createClass({

  displayName: 'EnrichmentSnippet',

  propTypes: {
    asset: React.PropTypes.object,
    title: React.PropTypes.string,
    description: React.PropTypes.string,
    placement: React.PropTypes.string
  },

  render() {

    var { asset, title, description, placement } = this.props;

    return (
      <div {...classes(null, placement === 'left' ? 'aside' : 'full')}>
        <div {...classes('pandoras-box')} dangerouslySetInnerHTML={{ __html: asset.html }} />
        <Caption
          title={asset.title || title}
          description={description}
        />
      </div>
    );
  }

});
