import React from 'react';
import bem from 'react-bem-helper';

const classes = new bem({
  name: 'enrichment-text',
  prefix: ''
});

export default React.createClass({

  displayName: 'EnrichmentText',

  propTypes: {
    title: React.PropTypes.string.isRequired,
    description: React.PropTypes.string.isRequired,
    placement: React.PropTypes.string
  },

  render() {
    return (
      <div {...classes(null, this.props.placement === 'left' ? 'aside' : 'full')}>
        <h2 {...classes('title')}>{this.props.title}</h2>
        <div {...classes('body')} dangerouslySetInnerHTML={{ __html: this.props.description }} />
      </div>
    );
  }

});
