import React from 'react';
import bem from 'react-bem-helper';

import { getEnrichmentPlacement } from 'helpers/enrichment';

const classes = new bem({
  name: 'enrichment-text',
  prefix: ''
});

export default React.createClass({

  displayName: 'EnrichmentText',

  propTypes: {
    title: React.PropTypes.string,
    description: React.PropTypes.string.isRequired,
    placement: React.PropTypes.string
  },

  render() {
    const { title, description } = this.props;
    return (
      <div {...classes(null, getEnrichmentPlacement(this.props.placement))}>
        { title ? <h2 {...classes('title')}>{title}</h2> : null }
        <div {...classes('body')} dangerouslySetInnerHTML={{ __html: description }} />
      </div>
    );
  }

});
