import React from 'react';

import getAssetMeta from 'helpers/get-asset-meta';

export default Component =>  React.createClass({

  displayName: 'EnrichmentVideoWrapper',

  propTypes: {
    title: React.PropTypes.string.isRequired,
    description: React.PropTypes.string,
    source: React.PropTypes.object.isRequired,
    still_image: React.PropTypes.object.isRequired
  },

  mappedData(props) {
    return {
      kaltura_id: props.kaltura_id,
      caption: getAssetMeta(props),
      meta: {
        thumbnailUrl: props.asset.still_image.image_url || props.asset.still_image.asset.image_url
      },
      // accountID: "1789881",
      // playerID: "26921101",
      // playerWithoutAdsID: "29381931",
      // trackingURL: "https://aznetz.wemfbox.ch/cgi-bin/ivw/CP/aaz?r=http%3A//www.aargauerzeitung.ch/ressort/subressort/article-title-129298028&d=&x=",
      // autoPlay: false
    };
  },

  render() {
    return (
      <Component {...this.mappedData(this.props)} />
    );
  }

});
