import React from 'react';

export default React.createClass({

  displayName: 'Expandable',

  render() {
    var isExpanded = this.props.expanded ? ' is-expanded' : '';
    var className = 'expandable' + isExpanded;

    return (
      <div className={className} {...this.props}>
        {this.props.children}
      </div>
    );
  }

});
