import React from 'react';

export default React.createClass({

  displayName: 'Expandable',

  propTypes: {
    expanded: React.PropTypes.bool.isRequired,
    initialHeight: React.PropTypes.number
  },

  getDefaultProps() {
    return {
      initialHeight: 0
    };
  },

  getInitialState() {
    return {
      height: this.props.initialHeight
    };
  },

  componentWillUpdate(nextProps) {
    // only reset height if we change expansion state from expanded to contracted
    // do not reset if expansion state remains unchanged
    if (!nextProps.expanded && this.props.expanded !== nextProps.expanded) {
      this.resetHeight();
    }
  },

  resetHeight() {
    // reset to start value
    this.addExpanderHeight(0);
  },

  // @TODO: rename to setExpandableHeight
  addExpanderHeight(expanderHeight) {
    if (this.props.expanded) {
      // do not directly set style attributes. Instead use a state and set style in render method.
      this.setState({
        height: Math.round(this.props.initialHeight + expanderHeight)
      });
    }
  },

  // pass on properties to children by cloning react child element
  renderChildren() {
    return React.Children.map(this.props.children, (child) => {
      return React.cloneElement(child, {
        onAddExpanderHeight: this.addExpanderHeight
      });
    });
  },


  render() {
    var isExpanded = this.props.expanded ? ' is-expanded' : '';
    var className = 'expandable expandable-grid' + isExpanded;

    return (
      <div
        {...this.props}
        className={className}
        ref="expandable"
        style={{height: this.state.height + 'px'}}
      >
        {this.renderChildren()}
      </div>
    );
  }
});
