import React from 'react';

export default React.createClass({

  displayName: 'FooterInject',

  injectFooter() {
    return {__html: '<!--# include virtual="/__fetchurl__?url=http://www.azmedien.ch/footer/" -->'};
  },

  render() {
    return (
      <div dangerouslySetInnerHTML={this.injectFooter()} />
    );
  }

});
