/* eslint-disable no-unused-expressions */

import _ from 'lodash';
import slot from 'components/footer/slot';

import { expect } from 'chai';

describe('Footer', function() {

  describe('slot', function() {

    let firstSection, firstSectionData;

    beforeEach(function() {
      let sections = slot.data().sections;
      let firstKey = _.keys(sections)[0];
      firstSection = sections[firstKey];
      firstSectionData = firstSection[0];
    });

    it('sets iso rendering', function() {
      expect(slot.iso).to.be.true;
    });

    it('returns data', function() {
      expect(slot.data()).to.be.an('object');
    });

    it('returns sections', function() {
      expect(slot.data().sections).to.be.an('object');
    });

    it('with arrays of items', function() {
      expect(firstSection).to.be.an('array');
    });

    it('and name / url', function() {
      expect(firstSectionData).to.be.an('object');
      expect(_.has(firstSectionData, 'name')).to.be.true;
      expect(_.has(firstSectionData, 'url')).to.be.true;
    });

  });

});
