import React from 'react';
import bem from 'react-bem-helper';


const classes = new bem({
  name: 'form',
  prefix: ''
});

export default React.createClass({

  displayName: 'Form',

  render: function() {
    return <form {...classes()}><div className="form-row"><label className="form__label">Vorname</label><input type="text" name="input" className="form__input" placeholder="Maximilian" /></div><div className="form-row"><label className="form__label">Nachname</label><input type="text" name="input" className="form__input" placeholder="Schmidt" /></div><div className="form-row"><label className="form__label">Mitteilung</label><textarea rows="4" className="form__input form__input--textarea" placeholder="Ihre Nachricht"></textarea></div><div className="form-row"><input type="submit" className="form__submit button" /></div></form>;
  }

});
