import React from 'react';
import bem from 'react-bem-helper';
import TextInput from 'inputs/text';
import TextArea from 'inputs/text-area';

const classes = new bem('form');

export default React.createClass({

  displayName: 'Form',

  onChange(key, value) {
    this.setState({
      [key]: value
    })
  },

  renderHint() {
    return (
      <span>
        forgot your password?
        {' '}
        <a href="#">Reset it here</a>
      </span>
    );
  },

  render() {
    return (
      <form {...classes()}>
        <TextInput className="form-row" name="vorname" label="Vorname" onChange={this.onChange} placeholder="Maximilian"/>
        <TextInput className="form-row" name="nachname" label="Nachname" onChange={this.onChange} placeholder="Schmidt"/>
        <TextArea className="form-row" name="mitteilung" label="Mitteilung" onChange={this.onChange} placeholder="Ihre Nachricht" rows={4} hint={this.renderHint()} />
        <div className="form-row">
          <label className="form__label">Nutzungsbedingungen</label>
          <label for="checkbox_a">
            <input type="checkbox" id="checkbox_a" className="form__checkbox" name="Nutzungsbedingungen" value="Nutzungsbedingungen" />
            <span></span> Ich stimme den Nutzungsbedingungen zu.
          </label>
        </div>
        <div className="form-row">
          <label className="form__label">Optionen</label>
          <div className="form-row form-row--inner">
            <label for="option_a">
              <input type="radio" id="radio_a" className="form__radio" name="Optionen" value="Optionen" />
              <span></span> Option A.
            </label>
          </div>
          <div className="form-row form-row--inner">
            <label for="option_b">
              <input type="radio" id="radio_b" className="form__radio" name="Optionen" value="Optionen" />
              <span></span> Option B.
            </label>
          </div>
          <div className="form-row form-row--inner">
            <label for="option_c">
              <input type="radio" id="radio_c" className="form__radio" name="Optionen" value="Optionen" />
              <span></span> Option C.
            </label>
          </div>
        </div>
        <div className="form-row">
          <label className="form__label">Select</label>
          <div className="select-box">
            <select className="form__select">
              <option selected> Select Box </option>
              <option>Short Option</option>
              <option>This Is A Longer Option</option>
            </select>
          </div>
        </div>
        <div className="form-row">
          <input type="submit" className="form__submit button button--submit" />
        </div>
      </form>

    );
  }

});
