import React from 'react';
import bem from 'react-bem-helper';

import GalleryDataMap from 'higher-order/gallery-data-map';
import GalleryMixin from 'mixins/gallery';

import tapEventPlugin from 'react-tap-event-plugin';
tapEventPlugin();

const classes = new bem({
  name: 'gallery',
  prefix: ''
});

const Gallery = React.createClass({

  mixins: [GalleryMixin],

  propTypes: {
    title: React.PropTypes.string,
    description: React.PropTypes.string,
    images: React.PropTypes.array.isRequired
  },

  getInitialState() {
    return {
      currentIndex: 0,
      fullscreen: false
    };
  },

  render() {
    return (
      <div {...classes()}>
        <button type='button' onTouchTap={this.navigateSlider.bind(this, -1)}>Prev</button>
        <button type='button' onTouchTap={this.navigateSlider.bind(this, +1)}>Next</button>
        Currently showing {this.state.currentIndex + 1} from {this.props.images.length}
      </div>
    );
  }

});

export default GalleryDataMap(Gallery);
