import React from 'react';
import bem from 'react-bem-helper';

const classes = new bem({
  name: 'grid',
  prefix: ''
});

// For styleguide only
const GridItem = React.createClass({
  render() {
    return (
      <div {...classes('placeholder')}></div>
    );
  }
});

export default React.createClass({

  displayName: 'Grid',

  propTypes: {
    modifier: React.PropTypes.string,
    items: React.PropTypes.array
  },

  mapStyleguideItems(count) {
    var items = [], i = 0, len = count;
    while (++i <= len) items.push(<GridItem/>);
    return items;
  },

  render() {

    var items = this.props.items || this.mapStyleguideItems(this.props.count);

    return (
      <div {...classes(null, this.props.modifier)}>
        {items.map(function (item, index) {
          return (
            <div {...classes('item', ['count-' + (index + 1)])} key={index}>
              {item}
            </div>
          );
        })}
      </div>
    );
  }

});
