import React from 'react';
import bem from 'react-bem-helper';
import path from 'path';

const classes = new bem({
  name: 'header-logo',
  prefix: ''
});

export default React.createClass({

  displayName: 'HeaderLogo',

  propTypes: {
    siteId: React.PropTypes.string.isRequired
  },

  logoPath(siteId) {
    var prefix = process.env.STATIC_ASSETS || '/__node__/__static__/client';
    return path.join(prefix, 'images/logos', siteId + '.svg');
  },

  render() {

    var { siteId } = this.props;

    return (
      <div {...classes()}>
        <img src={this.logoPath(siteId)} className='logo' />
      </div>
    );
  }

});
